//clocks
clock globalTime;

int[0,randomness-1] randomSeed;
Cell_t grid[Nrows][Ncolumns];
Civilian_t civilians[Ncivilians];
Professional_t professionals[Nprofessionals];




int[0,Ncivilians] civInDanger=0;

//CAN PROFESSIONALS DIE??
int[0,Ncivilians] casualties;
int[0,Ncivilians] survivors;

//Coordinates helpers
int distance(Coordinates_t p1, Coordinates_t p2){
    return abs(p1.row-p2.row)+abs(p1.column-p2.column);
}

bool isSamePlace(Coordinates_t c1, Coordinates_t c2){
    return c1.row == c2.row && c1.column == c2.column;
}

//return the index of the help request
void neededHelpAt(Coordinates_t pos){
    civilians[grid[pos.row][pos.column].civilian].isInDanger=true;
    civInDanger++;
}

void noHelpNeededAt(Coordinates_t pos){
    civilians[grid[pos.row][pos.column].civilian].isInDanger=false;
    civInDanger--;
}

void noHelpNeeded(id_civ_t idCivilian){
    civilians[idCivilian].isInDanger=false;
    civInDanger--;
}


//even in diagonal?
bool isCloseToFire(Coordinates_t point){
    int x,y;
    if(grid[point.row][point.column].onFire)
        return true;
    for(i : int[-1,1]){
        for(j: int[-1,1]){
            x= point.row+i;
            y= point.column+j;
            if(x>=0 && x< Nrows  &&  y>=0 && y<Ncolumns){
                if(grid[x][y].onFire)
                    return true;
            }

        }
    }
    return false;
}

//even in diagonal?
bool isCloseToExit(Coordinates_t point){
    int x,y;
    if(grid[point.row][point.column].isExit)
        return true;
    for(i : int[-1,1]){
        for(j: int[-1,1]){
            x= point.row+i;
            y= point.column+j;
            if(x>=0 && x< Nrows  &&  y>=0 && y<Ncolumns){
                if(grid[x][y].isExit)
                    return true;
            }

        }
    }
    return false;
}




//MOVING STRATEGIES

bool isValidPosition(int row,int column){
    Cell_t cell;

    if(row < 0 || column < 0)
        return false;
    if(row > Nrows-1 || column > Ncolumns-1)
        return false;

    cell = grid[row][column];

    if(cell.civilian>=0 || cell.professional>=0)
        return false;

    //should never happen
    if(cell.onFire)
        return false;

    return true;

}

//id used for randomness
Coordinates_t randomMove(Coordinates_t currentPos, int id){
    bool validMove;
    int i=0;
    int inc;
    int rand;
    int x,y;
    Coordinates_t move;

    rand=(randomSeed ^ id) % 2;
    if (rand == 0)
        inc = +1;
    else
        inc = -1;

    randomSeed=(randomSeed+id) % randomness;

    rand = (randomSeed ^ id) % 4;

    randomSeed=(randomSeed+id) % randomness;
    do{
        y=currentPos.row;
        x=currentPos.column;

        //switch case not supperted
        if(rand == 0)
            //up
            y--;
        else if(rand == 1)
            //right
            x++;
        else if(rand == 2)
            //down
            y++;
        else if(rand == 3)
            //left
            x--;

        validMove=isValidPosition(y,x);

        if(!validMove){
            rand = (rand + inc) % 4;
            i++;
        }    


    }while(!validMove && i<4);

    if(validMove){
        move.row=y;
        move.column=x;
    }
    else
        //if no available moves
        move=currentPos;

    return move;

}

Coordinates_t moveToExit(Coordinates_t currentPos,Coordinates_t exitPos,int id){
     Coordinates_t move;
     int row = currentPos.row;
     int column = currentPos.column;

     if(abs(currentPos.row-exitPos.row) > abs(currentPos.column-exitPos.column)) {
        //precedence to row movement
        if(currentPos.row-exitPos.row > 0){
            row--; 
        }else{
            row++;
        }
        if(isValidPosition(row,column)){
                move.row=row;
                move.column=column;
                return move;
        }else{
            row=currentPos.row;
            if(currentPos.column-exitPos.column > 0){
                column --;
            }else{
                column ++;
            }
            if(isValidPosition(row,column)){
                move.row=row;
                move.column=column;
                return move;
            }else{
                return randomMove(currentPos,id);
            }
        }

    }else{
        //precedence to column movement
        if(currentPos.column-exitPos.column > 0){
            column--; 
        }else{
            column++;
        }
        if(isValidPosition(row,column)){
                move.row=row;
                move.column=column;
                return move;
        }else{
            column=currentPos.column;
            if(currentPos.row-exitPos.row > 0){
                row --;
            }else{
                row ++;
            }
            if(isValidPosition(row,column)){
                move.row=row;
                move.column=column;
                return move;
            }else{
                return randomMove(currentPos,id);
            }


        }

    }
}

Coordinates_t getClosestExit(Coordinates_t pos){
    Coordinates_t min;
    int minDistance;
    int temp;
    int i;
    min= initExits[0];//suppose at least an exit
    minDistance = distance(pos,min);

    for( i=1; i<Nexits-1; i++){
        temp=distance(pos,initExits[i]);
        if(temp<minDistance){
            minDistance=temp;
            min=initExits[i];
        }      
    }

    return min;

}

template.declaration:
Coordinates_t currentPos;
id_civ_t civToHelp=0;
int timeToSave=0;

clock helpingTimer;


void move(){

    Coordinates_t pos;
    pos = randomMove(currentPos,id);

    grid[currentPos.row][currentPos.column].professional=-1;
    grid[pos.row][pos.column].professional=id;
    currentPos=pos;
}

bool someoneNearToHelp(){
    int i;
    for(i=0;i<Ncivilians;i++){
        if(civilians[i].isInDanger  && !civilians[i].isBusy && civilians[i].isOnTheGrid){
            if(distance(currentPos, civilians[i].currentPosition)<=1){
                return true;
            }
        }
    }
    return false;
}

void chooseCivToHelp(){
    int i,distanceToCiv;
    for(i=0;i<Ncivilians;i++){
        if(civilians[i].isInDanger && !civilians[i].isBusy && civilians[i].isOnTheGrid){
            distanceToCiv=distance(currentPos, civilians[i].currentPosition);
            if(distanceToCiv <=1){
                civToHelp=i;
                timeToSave=distanceToCiv + proAssistanceTime;
                civilians[i].isBusy=true;
                professionals[id].isBusy=true;
                return;
            }
        }
    }

}

Coordinates_t currentPos;

bool isGoingBack=false;

id_civ_t closeVictims[Ncivilians];
id_civ_t closeSurvivors[Ncivilians];

int[0,Ncivilians] victimsDetected=0,survivorsDetected=0;

typedef struct{
    bool doNothing;
    bool callProfessional;
    id_pro_t professional;
    id_civ_t survivor;
    id_civ_t victim;
}action_t;

action_t action;

Coordinates_t moveHorizontally(Coordinates_t currPos){
    Coordinates_t move;
    move.row=currPos.row;
    move.column=currPos.column;

    if(isGoingBack){
        if(currPos.column - droneRadius <=0){
            isGoingBack=false;
            move.column++;
            return move;
        }
        move.column--;

    }else{
        if(currPos.column + droneRadius >= Ncolumns-1){
            isGoingBack=true;
            move.column--;
            return move;
        }
        move.column++;
    }

    return move;

}

Coordinates_t moveVertically(Coordinates_t currPos){
    Coordinates_t move;
    move.row=currPos.row;
    move.column=currPos.column;

    if(isGoingBack){
        if(currPos.row - droneRadius <=0){
            isGoingBack=false;
            move.row++;
            return move;
        }
        move.row--;

    }else{
        if(currPos.row + droneRadius >= Nrows-1){
            isGoingBack=true;
            move.row--;
            return move;
        }
        move.row++;
    }

    return move;

}

//what happens if 2 drones occupy the same cell?
void move(){
    Coordinates_t pos;
    pos = moveHorizontally(currentPos);
    grid[currentPos.row][currentPos.column].drone=-1;
    grid[pos.row][pos.column].drone=id;
    currentPos=pos;
}


//busy civ?
void detectCivInDanger(){
    int i;
    for(i=0;i<Ncivilians;i++){
        victimsDetected=0;
        survivorsDetected=0;
            if(distance(civilians[i].currentPosition,currentPos)<= droneRadius){
                if(civilians[i].isInDanger && !civilians[i].isBusy){
                    closeVictims[victimsDetected]=i;
                    victimsDetected++;
                }else if(!civilians[i].isInDanger && !civilians[i].isBusy && civilians[i].isOnTheGrid){
                    closeSurvivors[survivorsDetected]=i;
                    survivorsDetected++;
                }
            }

    }

}


int getClosestFreePro(Coordinates_t position){
    int minDistance=Nrows+Ncolumns;
    int pro=-1;
    for(i : id_pro_t){
        if(!professionals[i].isBusy && distance(position,professionals[i].currentPosition) < minDistance){
            pro=i;
            minDistance=distance(position,professionals[i].currentPosition);
        }
    }
    return pro;

}

id_civ_t getClosestSurvivor(Coordinates_t position){
    int minDistance=Nrows+Ncolumns;
    id_civ_t civ,survivor;
    int i;
    for(i=0;i<survivorsDetected;i++){
        survivor=closeSurvivors[i];
        if(distance(position,civilians[survivor].currentPosition) < minDistance){
            civ=survivor;
            minDistance=distance(position,civilians[survivor].currentPosition);
        }
    }
    return civ;

}



void evaluateAction(){
    if(victimsDetected>0 && survivorsDetected>0){
        id_civ_t victim,survivor;
        id_pro_t pro;
        int findPro;
        victim=closeVictims[0];
        survivor=getClosestSurvivor(civilians[victim].currentPosition);
        findPro=getClosestFreePro(civilians[survivor].currentPosition);
        if(pro>=0){
            pro=findPro;
            if(distance(professionals[pro].currentPosition,civilians[survivor].currentPosition)<=3){
                action.survivor=survivor;
                action.victim=victim;
                action.professional=pro;
                action.callProfessional=true;
                action.doNothing=false;
            }else{
                action.survivor=survivor;
                action.victim=victim;
                action.callProfessional=false;
                action.doNothing=false;
            }
        }
    }else
        action.doNothing=true;
}

void updateDetectedList(){
    int i;
    bool found=false;
    for(i=0;i<victimsDetected-1;i++){
        if(found){
            closeVictims[i]=closeVictims[i+1];
        }else if(closeVictims[i]==action.victim){
            found=true;
        }
    }
    if(found) victimsDetected--;

    found=false;
    for(i=0;i<survivorsDetected-1;i++){
        if(found){
            closeSurvivors[i]=closeSurvivors[i+1];
        }else if(closeSurvivors[i]==action.survivor){
            found=true;
        }
    }
    if(found) survivorsDetected--;


}

void clearDetectionData(){
    id_civ_t emptyCiv;
    action_t emptyAction;
    int i;
    for(i=0; i<victimsDetected; i++){
        closeVictims[i]=emptyCiv;
    }
    for(i=0; i<survivorsDetected; i++){
        closeVictims[i]=emptyCiv;
    }
    victimsDetected=0;
    survivorsDetected=0;
    action=emptyAction;

}

